definition.script:
pipeline {
    agent any

    tools {
        nodejs "NodeJS20"
    }

    environment {
        GITHUB_REPO = "https://github.com/Elink69/prf_beadando.git"
        BRANCH = "main"
    }

    stages {
        stage('Checkout'){
            steps {
                git branch: env.BRANCH, url: env.GITHUB_REPO
            }
        }

        stage('Install Server Dependencies'){
            steps {
                sh "cd app/server && npm ci"
            }
        }

        stage('Test Server'){
            steps {
                sh "cd app/server && npm run test"
            }
        }

        stage('Build Server'){
            steps {
                sh "cd app/server && npm run build"
            }
        }

        stage('Archive Server Artifacts'){
            steps {
                archiveArtifacts artifacts: 'app/server/build/**/*', fingerprint: true
            }
        }

        stage('Install Client Dependencies'){
            steps {
                sh "cd app/client && npm ci"
            }
        }

        stage('Test Client'){
            steps {
                sh "cd app/client && npm run test"
            }
        }

        stage('Build Client'){
            steps {
                sh "cd app/client && npm run build"
            }
        }

        stage('Archive Client Artifacts'){
            steps {
                archiveArtifacts artifacts: 'app/client/dist/**/*', fingerprint: true
            }
        }
    }
}
